mapper.select:
SELECT p.*, c.name AS category_name FROM product p 
LEFT JOIN category c ON p.category_id = c.id 
WHERE p.id = #{id}

mapper.update:
UPDATE product SET name = #{name}, category_id = #{categoryId}, price = #{price}, 
stock = #{stock}, image = #{image}, description = #{description}, status = #{status} 
WHERE id = #{id}
